import './App.css';
import axios from 'axios';

// src/App.js
import React, { useState, useEffect } from 'react';
import { checkEmails, sendEmail} from './services/api'; // Backend API functions

function LoginComponent() {
  const handleLogin = () => {
    // Redirect to the Flask /login route
    window.location.href = 'http://127.0.0.1:5000/login';  // Change localhost to your Flask backend URL
  };

  return (
    <div>
      <h2>Login</h2>
      <button onClick={handleLogin} className="bg-blue-500 text-white p-2 rounded">
        Login with Gmail
      </button>
    </div>
  );
}

function ChatComponent({ messages }) {
  return (
    <div>
      <h2>Chat</h2>
      <div>
        {messages.map((message, index) => (
          <p key={index}>{message.role}: {message.content}</p>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userProfile, setUserProfile] = useState({});
  const [emailSummary, setEmailSummary] = useState('');
  const [input, setInput] = useState('');

  // Check for login status
  useEffect(() => {
    // Check if user is logged in through backend session
    axios.get('http://127.0.0.1:5000/check_login')
      .then(response => {
        if (response.data.logged_in) {
          setIsLoggedIn(true);  // Set the user as logged in
        } else {
          setIsLoggedIn(false);  // Not logged in
        }
      })
      .catch(error => {
        console.error('Error checking login status:', error);
      });
  }, []);

  const handleCheckEmails = async () => {
    const emails = await checkEmails(); // Fetch emails from Gmail API via backend
    setEmailSummary(emails.summary); // Get summarized emails
    setMessages((prev) => [...prev, { role: 'assistant', content: emails.summary }]);
  };

  const handleSendEmail = async () => {
    const response = await sendEmail(input, userProfile); // Send email through Gmail API
    setMessages((prev) => [...prev, { role: 'user', content: input }]);
    setMessages((prev) => [...prev, { role: 'assistant', content: 'Email sent!' }]);
    setInput('');
  };

  return (
    <div className="container mx-auto p-6">
      {!isLoggedIn ? (
        <LoginComponent onLogin={setIsLoggedIn} />
      ) : (
        <div>
          <ChatComponent messages={messages} />
          <div className="my-4">
            <button
              onClick={handleCheckEmails}
              className="bg-blue-500 text-white p-2 rounded"
            >
              Check Emails
            </button>
          </div>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="border p-2 w-full"
          />
          <button
            onClick={handleSendEmail}
            className="bg-green-500 text-white p-2 rounded mt-4"
          >
            Send Email
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
